import { useState } from "react";
import contacts from "../contacts.json";


const FirstContacts = () => {

    const fiveContacts = contacts.slice(0, 5);
    // console.log (contactsArr);

    const [contactsArr, setContactArr] = useState(fiveContacts)

    const contactsRestArr = contacts.slice(5, (contacts.length));

    const addNewContacts = () => {
        let indexInContactsRestArr = Math.floor(Math.random() * contactsRestArr.length) - 1;
        let newContact = contactsRestArr.splice(indexInContactsRestArr, 1)
        // console.log(newContact)    
        setContactArr([...contactsArr, newContact[0]])
    }

    const sortByPopularity = () => {
        let copyContacts = [...contactsArr]
        setContactArr(() => {
            copyContacts.sort((a, b) => b.popularity - a.popularity)
