              return -1;
            }
            if (nameA > nameB) {
              return 1;
            }
            // names must be equal
            return 0;
          })
          return copy;
        });
      }



    return (
        <div>
            <button onClick={addNewContacts} >Add Random Contact</button>
            <button onClick={sortByPopularity} >Sort by popularity</button>
            <button onClick={sortByName} >Sort by name</button>
            <table>
                <thead>
                    <tr>
                        <th>Picture</th>
                        <th>Name</th>
                        <th>Popularity</th>
                        <th>Won an Oscar</th>
                        <th>Won an Emmy</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {contactsArr.map((cont) => {
                        return (
                            <tr key={cont.id}>
                                <td><img src={cont.pictureUrl} alt={cont.name} /></td>
                                <td>{cont.name}</td>
                                <td>{cont.popularity.toFixed(2)}</td>
                                <td>{(cont.wonOscar) ? "üèÜ" : null}</td>
                                <td>{(cont.wonEmmy) ? "üåü" : null}</td>
                            </tr>
                        )
                    })

                    }
                </tbody>
            </table>
        </div>

    )





}

